import { useEffect, useRef, useState } from "react";

function Bat1() {
    const batRef = useRef(null);
    const [isDragging, setIsDragging] = useState(false);
    const [posY, setPosY] = useState(0);

    const handleMouseDown = () => {
        setIsDragging(true);
    };

    const handleMouseUp = () => {
        setIsDragging(false);
    };

    const handleMouseMove = (e) => {
        if (isDragging) {
            const newY = e.clientY - 80; // center paddle around mouse
            const containerHeight = window.innerHeight * 0.7; // match h-[70%]
            const minY = 0;
            const maxY = containerHeight - 160; // 160 is paddle height

            const clampedY = Math.max(minY, Math.min(maxY, newY));
            setPosY(clampedY);
        }
    };

    useEffect(() => {
        window.addEventListener("mousemove", handleMouseMove);
        window.addEventListener("mouseup", handleMouseUp);
        return () => {
            window.removeEventListener("mousemove", handleMouseMove);
            window.removeEventListener("mouseup", handleMouseUp);
        };
    }, [isDragging]);

    return (
        <div
            ref={batRef}
            onMouseDown={handleMouseDown}
            className="ml-8 w-[18px] h-[160px] border-4 border-neon rounded-xl shadow-neon shadow-[0_10px_40px_rgba(0,0,0,0.1)] bg-white absolute"
            style={{ top: `${posY}px` }}
        ></div>
    );
}

export default Bat1;
